import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useParams} from 'react-router-dom'; 

const DisplayList = () => {
    const [pets, setPets] = useState([]);
    const { id } = useParams();
    const navigate = useNavigate();
    const [like, setLike] = useState(false);
    
    useEffect(() => {
      axios
        .get('http://localhost:8000/api/pets')
        .then((res) => {
          console.log(res.data);
          setPets(res.data);
        })
        .catch((err) => console.log(err));
    }, []);
    const deletePet = (petId) => {
      axios
        .delete(`http://localhost:8000/api/movies/${id}`)
        .then((res) => {
          navigate('/');
        })
        .catch((err) => console.log(err));
    };
  
  return (
    <div className="container">
      <div className="col-8">
        <table class="table table-bordered">
          <thead>
            <tr>
              <th scope="col">Available</th>
              <th scope="col">Information</th>
            </tr>
          </thead>
          <tbody>
            {pets.map((pet) => (
            <tr key={pet._id} >
              <th scope="row">{pet.name} |
                  <button disabled={like} onClick={() => setLike(true)}> Like </button>
              </th>
              <td>
                <Link to={`/pet/${pet._id}`}><button className="btn btn-primary">Details</button></Link>
                <Link to={`/pet/edit/${pet._id}`}><button className="btn btn-primary">update</button></Link>
                <button type="button" class="btn btn-success" onClick={deletePet}>Adopt</button>
              </td>
            </tr>   
          ))}
          </tbody>
        </table>
              <Link to={`/new`} type="button" className="btn btn-primary">Add a Pet</Link>
      </div>
    </div>
  );
};
  
export default DisplayList;
