import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const NewPet = () => {
    const [name, setName] = useState('');
    const [type, setType] = useState('');
    const [description, setDescription] = useState('');
    const [skills, setSkills] = useState(0);
    const navigate = useNavigate();


    const handleSubmit = (e) => {
        e.preventDefault();
        axios
        .post('http://localhost:8000/api/pets', {
            name,
            type,
            description,
            skills
        })
        .then((res) => {
            console.log(res.data);
            navigate('/');
        })
        .catch((err) => setErrors(err.response.data.errors));
    };
    return (
        <form onSubmit={handleSubmit}>
        <label>Pet Name</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        {errors.name && <span className="text-danger">{errors.name.message}</span>}
        <label>Type</label>
        <input type="text" value={type} onChange={(e) => setActors(e.target.value)} />
        {errors.type && <span className="text-danger">{errors.type.message}</span>}
        <label> Description</label>
        <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
        {errors.description && <span className="text-danger">{errors.description.message}</span>}
        <label> How Many Skills</label>
        <input type="number" value={skills} onChange={(e) => setSkills(e.target.value)} />
        {errors.skills && <span className="text-danger">{errors.skills.message}</span>}

        <button>Add Movie</button>
        </form>
    );
};

export default NewPet;
