import React, { useEffect, useState } from 'react'
import axios from 'axios';
import {useNavigate, useParams} from "react-router-dom";
import {Link} from 'react-router-dom';

const UpdatePet = () => {

    const { id } =useParams ();
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skills, setSkills] = useState(0);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:8000/api/pet/' + id)
            .then(response => {
                setName(response.data.name);
                setType(response.data.type);
                setDescription(response.data.description);
                setSkills(response.data.skills)
    
            })
            .catch((err) => {
                console.log(err.response);
            });
    }, [id])
    const handleSubmit = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8000/api/pet/${id}`, {
            name,
            type,
            description,
            skills,
        })
        .then((res) => {
            console.log(res.data);
            navigate('/');
        })
        .catch(err=>{
            setErrors(err.response.data.errors);
            console.log("help", err.response.data)
        })
    };
    return (
        <div>
            <h3 style={{color: "purple"}}>Update Pet</h3>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Pet Name</label>
                    <input type="text" name="name" value={name} onChange={(e) => { setName(e.target.value) }} /> 
                    { errors.name ? 
                        <p className="text-danger">{errors.name.message}</p>
                    : null
                    }       
                </div>
                <div>
                    <label>Type</label>
                    <input type="text" value={type} onChange={(e) => setType(e.target.value)} />
                    { errors.type ? 
                        <p className="text-danger">{errors.type.message}</p>
                    : null
                    }
                </div>
                <div>
                    <label>Description</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
                    { errors.description ? 
                        <p className="text-danger">{errors.description.message}</p>
                    : null
                    }
                </div>
                <div>
                    <label>Skills</label>
                    <input type="number" value={skills} onChange={(e) => setSkills(e.target.value)} />
                    { errors.skills ? 
                        <p className="text-danger">{errors.skills.message}</p>
                    : null
                    }
                </div>
                <button>Update Pet</button>
            </form> 
            <Link to={`/`} type="button"  class="btn btn-danger">Cancel</Link>   
        </div>
    )

};

export default UpdatePet
