import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; 

const DisplayList = () => {
    const [pets, setPets] = useState([]);
    
    const [like, setLike] = useState(false);
    
    useEffect(() => {
      axios
        .get('http://localhost:8000/api/pets')
        .then((res) => {
          console.log(res.data);
          setPets(res.data);
        })
        .catch((err) => console.log(err));
    }, []);
  
    return (
      <div className="container">
        {pets.map((pet) => (
          <div key={pet._id} class="row justify-content-md-center">
            <h2>{pet.name}</h2>
            <p>Currently Available to be adopted</p>
            
            <br />
            <Link to={`/pet/${pet._id}`}><button className="btn btn-primary">Details</button></Link>
            <button type="button" className="btn btn-primary" onClick={() => setLike((prevLike) => !prevLike)}> Like:{ like ? 1 : ''}</button>

          </div>
        ))}
        <Link to={`/new`} type="button" className="btn btn-primary">Add a Pet</Link>
      </div>
    );
  };
  
  export default DisplayList;
