import {BrowserRouter, Routes, Route} from 'react-router-dom';
import NewPet from './components/NewPet';
import DisplayList from './components/NewPet';
import PetDetails from './components/PetDetails';
import UpdatePet from './components/UpdatePet';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1 style={{color: "purple"}}>Pet Shelter</h1>
        <h4 style={{color: "blue"}}>Adopt a Pet Today</h4>
        <BrowserRouter>            
            <Routes>    
                  <Route element={<DisplayList/>} path="/" />
                  <Route element={<NewPet/>} path="/create" />
                  <Route element={<UpdatePet/>} path="/pet/edit/:id" />
                  <Route element={<PetDetails/>} path="/pet/:id" />
            </Routes>
          </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
