import { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const NewPet = () => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skills, setSkills] = useState(0);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();


    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/pet', {
        name, type, description, skills
        })
        .then((res) => {
            console.log(res.data);
            navigate('/');
        })
        .catch(err=>{
            setErrors(err.response.data.errors);
            console.log("help", err.response.data)
        })
    };
    return (
        <div>
            <Link to={`/`} type="button" className="btn btn-success">Home</Link>
        <form onSubmit={handleSubmit}>
            <div>
            <label>Name</label>
            <input type="text" value={name} onChange={(e) => setName (e.target.value)}/>
            
            { errors.name ? 
                        <p>{errors.name.message}</p>
                : null
            }
            <label>Type</label>
            <input type="text" value={type} onChange={(e) => setType(e.target.value)} />
            
            { errors.type ? 
                        <p>{errors.type.message}</p>
                : null
            }
            <label>description</label>
            <input type="text" value={description} onChange={(e) =>setDescription (e.target.value)} />
            { errors.description ? 
                        <p className="text-danger">{errors.description.message}</p>
                : null
            }
            <label>Skills</label>
            <input type="number" value={skills} onChange={(e) => setSkills (e.target.value)} />
            {errors.skills && <span className="text-danger">{errors.skills.message}</span>}
            <button type="submit" class="btn btn-primary">Add Pet</button>
            </div>
        </form>
        <Link to={`/`} type="button"  class="btn btn-danger">Cancel</Link>
    </div>
    );
};

export default NewPet;
