import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; 

const DisplayList = () => {
    const [pets, setPets] = useState([]);
    
    const [like, setLike] = useState(false);
    
    useEffect(() => {
      axios
        .get('http://localhost:8000/api/pets')
        .then((res) => {
          console.log(res.data);
          setPets(res.data);
        })
        .catch((err) => console.log(err));
    }, []);
  
    return (
      <table class="table">
        {pets.map((pet) => (
          <div key={pet._id} >
            <thead>
              <tr>
                <th scope="col">Available</th>
                <th scope="col">Information</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">{pet.name} ||
                    <button disabled={like} onClick={() => setLike(true)}> Like </button>
                </th>
                <td>
                  <Link to={`/pet/${pet._id}`}><button className="btn btn-primary">Details</button></Link>
                  <Link to={`/pet/edit/${pet._id}`}><button className="btn btn-primary">update</button></Link>
                </td>
              </tr>
            </tbody>
          </div>
        ))}
        <Link to={`/new`} type="button" className="btn btn-primary">Add a Pet</Link>
      </table>
    );
  };
  
  export default DisplayList;
